[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
        }
        .print-heading {
            border-bottom: 2px solid #000;
            padding-right: 10px;
            margin-bottom: 10px;
        }
        .print-heading img {
            max-height: 100px;
        }
        .contact-info {
            text-align: right;
        }
        .contact-info p {
            margin: 0;
        }
        .main-content {
            padding: 20px;
        }
    </style>
    <div class="print-heading">
        <div style="float: right; padding-bottom:10px;">
            <img src="/files/JKM Logo.png" alt="Company Logo"> <!-- Replace with actual logo -->
        </div>
        <div class="contact-info" style="float: left; text-align:left">
            <h2>JKM Chemtrade</h2>
            <p>109, Anupam Arcade, Canal Road,</p>
            <p>Parvat Patia, Surat-395010 Gujarat, India</p>
            <p>Email: Info@Jkmchemtrade.Com</p>
            <p>Website: www.jkmchemtrade.com</p>
        </div>
        <div style="clear: both; padding-top:10px;"></div>
    </div>
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<style>
.parent { 
    width: 100%;
    display: flex;
    justify-content: space-between;
} 
.child { 
    flex:1;
}
b{
    font-size:14px;
}
p{
    font-size:14px;
}
.tbspace>tbody>tr>td {
       padding: 0px 0px 0px 2px !important;
       margin: 0 !important;
       border-spacing: 0 !important;
   }
</style>

<table width="100%">
    <tr>
        <td>
            <p><b>Ship To</b></p>
            <p><b>{{ doc.party_name }}</b></p>
            {% if doc.address_link %}
            {% set address = frappe.get_doc("Address", doc.address_link) %}
            <p>
            {{ address.address_line1 }}
            </p>
            {% if address.address_line2 %}
                <p>{{ address.address_line2 }}</p>
                <p>{{ address.city or ''}} {{ address.state or  ''}} {{ address.pincode or '' }}</p>
            {% endif %}
            {% endif %}
            <p><p><b>Contact Person:</b> {{frappe.db.get_value("Contact", doc.contact_person, 'first_name') or  ''}} {{ frappe.db.get_value("Contact", doc.contact_person, 'last_name') or  '' }}</p>
            <p><b>Mobile No: </b>{{ doc.contact_mobile or ''}}</p>
        </td>
        <td>
            <p>Date: {{ frappe.utils.formatdate(doc.date, "dd-MM-yyyy") }}</p>
            <p><b>Sample Request Ref:</b> {{ frappe.db.get_value("Outward Sample", doc.sample_items[0].sample_ref, "inward_ref") or ''}}</p>
            {% set iws = frappe.db.get_value("Outward Sample", doc.sample_items[0].sample_ref, "inward_ref") or '' %}
            <p><b>Request Date:</b> {{ frappe.utils.formatdate(frappe.db.get_value("Inward Sample", iws, "date"), "dd-MM-YYYY") or '' }}</p>
        </td>
    </tr>
</table>

<br>
<h2>Subject: Free Sample as per your Request</h2>

<p style="margin-top:5px;"><b>Product Details</b></p>
{% if doc.has_sample %}
<table width="100%" border="1" class="tbspace">
    <tr>
        <th>Item Details</th>
        <th style="text-align:center;">Batch No</th>
        <th width="12%" style="text-align:center;">Net Weight</th>
        <th style="text-align:center;" width="12%">No of Packages</th>
        <th style="text-align:center;" width="15%">Total Net Weight</th>

    </tr>
    {% for row in doc.sample_items %}
    <tr>
    <td>
        <p>{{ row.item }}</p>
        <p>{{ row.product_name }}</p>
    </td>
    <td align="center"><p>{{ row.batch_no or ''}}</p></td>
    <td width="12%" align="center"><p>{{ row.quantity or ''}} {{ row.uom or ''}}</p></td>
    <td align="center" width="12%"><p>{{ row.no_of_packages or '' }}</p></td>
    <td align="center" width="15%"><p>{{ row.quantity * row.no_of_packages|flt }} {{ row.uom or ''}}</p></td>

    </tr>
    {% endfor %}
</table>
{% endif %}

<p style="margin-top:5px;"><b>Gross Weight :</b> {{ doc.weight or '' }} KG</p>

"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<p>Note:</p>
<p>These products hold no commercial value and are provided solely for sample and testing purposes.</p>
<br>
<p>Remarks:</p>
<p style="margin-top:5px;">{{ doc.remarks or ''}}</p>
<b>Documents Attached:</b>
    <ol>
    {% for row in doc.courier_items %}
        <li>{{ row.document }}</li>
    {% endfor %}
    </ol>
<p>We look forward to your feedback. Please feel free to contact us for any further assistance.</p>
<br>
<br>
<p>Regards,</p>
<p>For JKM Chemtrade</p>
<p>{{ doc.courier_delivered_by or ''}}</p>
<p>{{ doc.designation or ''}}</p>"}]
