[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
        }
        .print-heading {
            border-bottom: 2px solid #000;
            padding-right: 10px;
            margin-bottom: 10px;
        }
        .print-heading img {
            max-height: 100px;
        }
        .contact-info {
            text-align: right;
        }
        .contact-info p {
            margin: 0;
        }
        .main-content {
            padding: 20px;
        }
    </style>
    <div class="print-heading">
        <div style="float: right; padding-bottom:10px;">
            <img src="/files/JKM Logo.png" alt="Company Logo"> <!-- Replace with actual logo -->
        </div>
        <div class="contact-info" style="float: left; text-align:left">
            <h2>JKM Chemtrade</h2>
            <p>109, Anupam Arcade, Canal Road,</p>
            <p>Parvat Patia, Surat-395010 Gujarat, India</p>
            <p>Email: Info@Jkmchemtrade.Com</p>
            <p>Website: www.jkmchemtrade.com</p>
        </div>
        <div style="clear: both; padding-top:10px;"></div>
    </div>
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<style>
.parent { 
    width: 100%;
    display: flex;
    justify-content: space-between;
} 
.child { 
    flex:1;
}
b{
    font-size:14px;
}
p{
    font-size:14px;
}
</style>
<table width="100%">
    <tr>
        <td>
            <p><b>Customer:</b> {{ doc.party_name }}</p>
            <p><b>Address</b></p>
            <span>
                {{ doc.address }}
            </span>
        </td>
        <td>
            <p><b>Sample Request Ref:</b> {{ frappe.db.get_value("Outward Sample", doc.sample_items[0].sample_ref, "inward_ref") or ''}}</p>
            {% set iws = frappe.db.get_value("Outward Sample", doc.sample_items[0].sample_ref, "inward_ref") or '' %}
            <p><b>Request Date:</b> {{ frappe.db.get_value("Inward Sample", iws, "date") or '' }}</p>
            <p><b>Request by:</b> {{frappe.db.get_value("Outward Sample", doc.sample_items[0].sample_ref, "party_name") or ''}} </p>
            <br>
            <p><b>Contact Person: </b>{{frappe.db.get_value("Contact", doc.contact_person, 'first_name') or  ''}} {{ frappe.db.get_value("Contact", doc.contact_person, 'last_name') or  '' }}</p>
            <p><b>Mobile No: </b>{{ doc.contact_mobile or ''}}</p>
            <p><b>Email ID: </b>{{ doc.contact_email or ''}}</p>
        </td>
    </tr>
</table>

<hr>
<center><b>For any queries or further assistance, please do not hesitate to contact us at:</b></center>
<hr>
{% if doc.has_sample %}
<table width="100%" border="1">
    <tr>
        <th>Item Details</th>
        <th>Sample Size</th>
        <th>UOM</th>
        <th>Batch No</th>
        <th>MFG Date</th>
    </tr>
    {% for row in doc.sample_items %}
    <tr>
    <td>
        <p>{{ row.item }}</p>
        <p>{{ row.product_name }}</p>
    </td>
    <td>{{ row.quantity or ''}}</td>
    <td>{{ row.uom or ''}}</td>
    <td>{{ row.batch_no or ''}}</td>
    <td>{{ row.manufacturing_date or '' }}</td>
    </tr>
    {% endfor %}
</table>
{% endif %}
<br>
<p>Kindly acknowledge the receipt and advice us your comments on the quality</p>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": ""}]
